package com.operation;

public class TernaryOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int februaryDays = 29;
		String result;

		// ternary operator
		result = (februaryDays == 28) ? "Not a leap year" : "Leap year";
		System.out.println(result);
	}

}
